'use client';

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import GapAnalysisDisplay from '../../components/analysis/GapAnalysisDisplay';
import CoverLetterGenerator from '../../components/analysis/CoverLetterGenerator';
import { createClient } from '@/utils/supabase/client';
import toast from 'react-hot-toast';

export default function AnalysisPage() {
  const [cvText, setCvText] = useState('');
  const [jdText, setJdText] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch latest CV and JD on mount
    const fetchData = async () => {
      try {
        const supabase = createClient();
        const { data: { session } } = await supabase.auth.getSession();
        const token = session?.access_token;
        
        if (!token) {
            console.log("No token found in AnalysisPage useEffect");
            return;
        }

        const config = {
          headers: { Authorization: `Bearer ${token}` }
        };

        // Fetch CV
        try {
          console.log("Fetching latest CV...");
          const cvRes = await axios.get('http://127.0.0.1:8000/api/cv/latest', config);
          console.log("CV Response:", cvRes.data);
          if (cvRes.data && cvRes.data.extracted_text) {
            setCvText(cvRes.data.extracted_text);
            toast.success('Loaded latest CV');
          } else {
             console.log("CV found but no extracted_text");
          }
        } catch (e: any) {
            console.error("Error fetching CV:", e.response?.data || e.message);
        }

        // Fetch JD
        try {
          console.log("Fetching latest JD...");
          const jdRes = await axios.get('http://127.0.0.1:8000/api/job-description/latest', config);
          console.log("JD Response:", jdRes.data);
          if (jdRes.data && jdRes.data.content) {
            setJdText(jdRes.data.content);
            toast.success('Loaded latest Job Description');
          }
        } catch (e: any) {
            console.error("Error fetching JD:", e.response?.data || e.message);
        }

      } catch (err) {
        console.error("Error loading data", err);
      }
    };
    fetchData();
  }, []);

  const handleAnalyze = async () => {
    setLoading(true);
    setError(null);
    try {
      const supabase = createClient();
      const { data: { session } } = await supabase.auth.getSession();
      const token = session?.access_token;
      
      const headers = token ? { Authorization: `Bearer ${token}` } : {};

      const response = await axios.post('http://127.0.0.1:8000/ai/analyze-gap/', {
        cv_text: cvText,
        job_description: jdText
      }, { headers });
      
      setResult(response.data);
    } catch (err: any) {
      setError(err.message || 'An error occurred');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4 md:p-8 max-w-7xl">
      <h1 className="text-3xl font-bold mb-6 text-white">Application Analysis</h1>
      
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Left Column: Inputs and Analysis */}
        <div className="space-y-6">
          <div className="bg-white p-6 rounded-lg shadow-sm">
            <h2 className="text-xl font-semibold mb-4 text-gray-700">1. Inputs</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Your CV Text</label>
                <textarea
                  className="w-full h-48 p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-800 bg-white placeholder-gray-400"
                  value={cvText}
                  onChange={(e) => setCvText(e.target.value)}
                  placeholder="Paste your CV content here..."
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Job Description</label>
                <textarea
                  className="w-full h-48 p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-800 bg-white placeholder-gray-400"
                  value={jdText}
                  onChange={(e) => setJdText(e.target.value)}
                  placeholder="Paste the job description here..."
                />
              </div>
              
              <button
                onClick={handleAnalyze}
                disabled={loading || !cvText || !jdText}
                className="w-full bg-blue-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-700 disabled:bg-gray-400 transition"
              >
                {loading ? 'Analyzing...' : 'Analyze Gap'}
              </button>
            </div>
          </div>

          <GapAnalysisDisplay result={result} isLoading={loading} error={error} />
        </div>

        {/* Right Column: Generated Letter */}
        <div className="lg:border-l lg:pl-8 lg:border-gray-200">
           {(cvText && jdText) ? (
             <CoverLetterGenerator cvText={cvText} jdText={jdText} />
           ) : (
             <div className="bg-gray-50 p-6 rounded-lg border-2 border-dashed border-gray-300 text-center text-gray-500 h-full flex items-center justify-center">
                <p>Enter your CV and a Job Description to generate a cover letter.</p>
             </div>
           )}
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState } from 'react';
import axios from 'axios';
import { createClient } from '@/utils/supabase/client';
import toast from 'react-hot-toast';
import { Copy, Check, Download } from 'lucide-react';

interface CoverLetterGeneratorProps {
  cvText: string;
  jdText: string;
}

const CoverLetterGenerator: React.FC<CoverLetterGeneratorProps> = ({ cvText, jdText }) => {
  const [generatedLetter, setGeneratedLetter] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [isCopied, setIsCopied] = useState(false);

  const handleGenerate = async () => {
    setLoading(true);
    setError(null);
    setGeneratedLetter('');

    try {
      const supabase = createClient();
      const { data: { session } } = await supabase.auth.getSession();
      const token = session?.access_token;
      
      const headers = token ? { Authorization: `Bearer ${token}` } : {};

      const response = await axios.post('http://127.0.0.1:8000/api/v1/generation/cover-letter', {
        cv_text: cvText,
        job_description_text: jdText
      }, { headers });

      if (response.data && response.data.cover_letter) {
        setGeneratedLetter(response.data.cover_letter);
        toast.success('Cover letter generated!');
      } else {
        throw new Error('No cover letter returned');
      }
    } catch (err: any) {
      console.error(err);
      setError(err.response?.data?.detail || err.message || 'Failed to generate cover letter');
      toast.error('Generation failed');
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = async () => {
    if (!generatedLetter) return;
    try {
      await navigator.clipboard.writeText(generatedLetter);
      toast.success('Copied to clipboard!');
      setIsCopied(true);
      setTimeout(() => setIsCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
      toast.error('Failed to copy to clipboard');
    }
  };

  const handleDownload = () => {
    if (!generatedLetter) return;
    
    try {
      const blob = new Blob([generatedLetter], { type: 'text/plain' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'cover-letter.txt';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
      toast.success('Downloaded!');
    } catch (err) {
      console.error('Failed to download:', err);
      toast.error('Failed to download file');
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Cover Letter Assistant</h2>
      
      {!generatedLetter && (
        <div className="text-center">
          <p className="mb-4 text-gray-600">Generate a tailored cover letter in Norwegian based on your CV and the job description.</p>
          <button
            onClick={handleGenerate}
            disabled={loading || !cvText || !jdText}
            className="bg-green-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-green-700 disabled:bg-gray-400 transition flex items-center justify-center mx-auto min-h-[44px]"
          >
            {loading ? (
              <>
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Generating... (this may take up to 2 mins)
              </>
            ) : 'Generate Cover Letter'}
          </button>
        </div>
      )}

      {error && (
        <div className="mt-4 p-4 bg-red-100 text-red-700 rounded-lg">
          {error}
        </div>
      )}

      {generatedLetter && (
        <div className="mt-6 animate-fade-in">
          <h3 className="text-lg font-semibold mb-2 text-gray-700">Generated Cover Letter</h3>
          <textarea
            className="w-full h-96 p-4 border rounded-lg focus:ring-2 focus:ring-green-500 text-gray-800 font-serif leading-relaxed"
            value={generatedLetter}
            onChange={(e) => setGeneratedLetter(e.target.value)}
          />
          <div className="mt-4 flex flex-wrap gap-4 justify-end">
             <button
              onClick={handleCopy}
              className="px-4 py-3 text-gray-600 hover:text-gray-900 border border-gray-300 rounded-md hover:bg-gray-50 flex items-center space-x-2 min-h-[44px]"
            >
              {isCopied ? <Check className="h-4 w-4 text-green-500" /> : <Copy className="h-4 w-4" />}
              <span>{isCopied ? 'Copied!' : 'Copy to Clipboard'}</span>
            </button>
            <button
              onClick={handleDownload}
              className="px-4 py-3 text-gray-600 hover:text-gray-900 border border-gray-300 rounded-md hover:bg-gray-50 flex items-center space-x-2 min-h-[44px]"
            >
              <Download className="h-4 w-4" />
              <span>Download .txt</span>
            </button>
             <button
              onClick={() => setGeneratedLetter('')}
              className="px-4 py-3 text-gray-500 hover:text-gray-700 min-h-[44px]"
            >
              Start Over
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CoverLetterGenerator;

import { defineConfig, devices } from '@playwright/test';

export default defineConfig({
  testDir: './__tests__/e2e',
  fullyParallel: true,
  forbidOnly: !!process.env.CI,
  retries: process.env.CI ? 2 : 0,
  workers: process.env.CI ? 1 : undefined,
  reporter: 'list',
  use: {
    baseURL: 'http://localhost:3000',
    trace: 'on-first-retry',
  },
  webServer: {
    command: 'npm run dev',
    url: 'http://localhost:3000',
    reuseExistingServer: !process.env.CI,
    timeout: 120 * 1000,
  },
  projects: [
    {
      name: 'Desktop Chrome',
      use: { ...devices['Desktop Chrome'] },
    },
    {
      name: 'Mobile Chrome',
      use: { ...devices['Pixel 5'] },
    },
    {
      name: 'Tablet Chrome',
      use: { ...devices['iPad Pro 11'] },
    },
  ],
});

import { test, expect } from '@playwright/test';

test.describe('Responsive Design', () => {

  test('Landing Page Responsive Layout', async ({ page }) => {
    await page.goto('/');

    // Check header/navbar
    const navbar = page.locator('nav');
    await expect(navbar).toBeVisible();

    const isMobile = (page.viewportSize()?.width || 0) < 768;

    if (isMobile) {
      // Mobile: Hamburger menu should be visible
      const menuButton = page.locator('button[aria-controls="mobile-menu"]');
      await expect(menuButton).toBeVisible();

      // Desktop links should be hidden
      await expect(page.locator('a:has-text("Log In")').first()).not.toBeVisible();
    } else {
      // Desktop: Hamburger menu should be hidden
      const menuButton = page.locator('button[aria-controls="mobile-menu"]');
      await expect(menuButton).toBeHidden();

      // Desktop links should be visible
      // Note: "Log In" might be inside the desktop menu div
      // Escaping colon for tailwind class: .md:flex -> .md\\:flex
      await expect(page.locator('.hidden.md\\:flex a[href="/login"]')).toBeVisible();
    }

    // Check Main Content Padding
    // It's hard to check padding values directly without screenshot or computed style, 
    // but we can check if content is visible and no horizontal scroll
    const main = page.locator('main');
    await expect(main).toBeVisible();
  });

  test('Login Page Responsive', async ({ page }) => {
    await page.goto('/login');
    // Just ensure it loads without error and form is visible
    // Depending on whether /login exists (it might be managed by Supabase Auth UI or custom page)
    // If it redirects to Supabase hosted UI, this might fail if strictly checking local elements.
    // Assuming local login page per project structure.
  });

});

