'use client';

import { useState } from 'react';
import Link from 'next/link';
import { Menu, X, User } from 'lucide-react';
import { signOut } from '@/app/login/actions';

interface NavbarProps {
  user: { email?: string } | null;
}

export default function Navbar({ user }: NavbarProps) {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className="bg-white border-b border-gray-200 shadow-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex">
            <Link href="/" className="flex-shrink-0 flex items-center outline-none focus-visible:ring-2 focus-visible:ring-blue-600 rounded-md">
              <span className="text-xl font-bold text-blue-600">AI Cover Letter</span>
            </Link>
          </div>

          {/* Desktop Menu */}
          <div className="hidden md:flex md:items-center md:space-x-4">
            {user ? (
              <>
                <Link href="/dashboard" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-sm font-medium outline-none focus-visible:ring-2 focus-visible:ring-blue-600">
                  Dashboard
                </Link>
                <form action={signOut}>
                  <button type="submit" className="text-gray-700 hover:text-red-600 px-3 py-2 rounded-md text-sm font-medium outline-none focus-visible:ring-2 focus-visible:ring-red-600">
                    Sign Out
                  </button>
                </form>
                <div className="flex items-center text-gray-500 ml-4">
                    <User className="h-5 w-5 mr-1" />
                    <span className="text-xs">{user.email}</span>
                </div>
              </>
            ) : (
              <Link href="/login" className="bg-blue-600 text-white hover:bg-blue-700 px-4 py-2 rounded-md text-sm font-medium outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-blue-600">
                Log In
              </Link>
            )}
          </div>

          {/* Mobile menu button */}
          <div className="flex items-center md:hidden">
            <button
              onClick={toggleMenu}
              type="button"
              className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-blue-500"
              aria-controls="mobile-menu"
              aria-expanded={isMenuOpen}
              aria-label="Main menu"
            >
              <span className="sr-only">Open main menu</span>
              {isMenuOpen ? (
                <X className="block h-6 w-6" aria-hidden="true" />
              ) : (
                <Menu className="block h-6 w-6" aria-hidden="true" />
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {isMenuOpen && (
        <div className="md:hidden" id="mobile-menu">
          <div className="pt-2 pb-3 space-y-1 px-4">
            {user ? (
              <>
                <div className="flex items-center px-3 py-2 text-gray-500 border-b border-gray-100 mb-2">
                    <User className="h-5 w-5 mr-2" />
                    <span className="text-sm font-medium">{user.email}</span>
                </div>
                <Link
                  href="/dashboard"
                  onClick={() => setIsMenuOpen(false)}
                  className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-blue-600 hover:bg-gray-50"
                >
                  Dashboard
                </Link>
                <form action={signOut} className="w-full">
                  <button
                    type="submit"
                    className="block w-full text-left px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-red-600 hover:bg-gray-50"
                  >
                    Sign Out
                  </button>
                </form>
              </>
            ) : (
              <Link
                href="/login"
                onClick={() => setIsMenuOpen(false)}
                className="block w-full text-center px-3 py-2 rounded-md text-base font-medium bg-blue-600 text-white hover:bg-blue-700"
              >
                Log In
              </Link>
            )}
          </div>
        </div>
      )}
    </nav>
  );
}

'use client';

import React, { useState } from 'react';
import axios from 'axios';
import { createClient } from '@/utils/supabase/client';
import toast from 'react-hot-toast';
import { Copy, Check, Download } from 'lucide-react';

interface CoverLetterGeneratorProps {
  cvText: string;
  jdText: string;
}

const CoverLetterGenerator: React.FC<CoverLetterGeneratorProps> = ({ cvText, jdText }) => {
  const [generatedLetter, setGeneratedLetter] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [isCopied, setIsCopied] = useState(false);

  const API_URL = process.env.NEXT_PUBLIC_API_URL || 'http://127.0.0.1:8000';

  const handleGenerate = async () => {
    setLoading(true);
    setError(null);
    setGeneratedLetter('');

    try {
      const supabase = createClient();
      const { data: { session } } = await supabase.auth.getSession();
      const token = session?.access_token;
      
      const headers = token ? { Authorization: `Bearer ${token}` } : {};

      const response = await axios.post(`${API_URL}/api/v1/generation/cover-letter`, {
        cv_text: cvText,
        job_description_text: jdText
      }, { headers });

      if (response.data && response.data.cover_letter) {
        setGeneratedLetter(response.data.cover_letter);
        toast.success('Cover letter generated!');
      } else {
        throw new Error('No cover letter returned');
      }
    } catch (err: any) {
      console.error(err);
      setError(err.response?.data?.detail || err.message || 'Failed to generate cover letter');
      toast.error('Generation failed');
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = async () => {
    if (!generatedLetter) return;
    try {
      await navigator.clipboard.writeText(generatedLetter);
      toast.success('Copied to clipboard!');
      setIsCopied(true);
      setTimeout(() => setIsCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
      toast.error('Failed to copy to clipboard');
    }
  };

  const handleDownload = () => {
    if (!generatedLetter) return;
    
    try {
      const blob = new Blob([generatedLetter], { type: 'text/plain' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'cover-letter.txt';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
      toast.success('Downloaded!');
    } catch (err) {
      console.error('Failed to download:', err);
      toast.error('Failed to download file');
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Cover Letter Assistant</h2>
      
      {!generatedLetter && (
        <div className="text-center">
          <p className="mb-4 text-gray-600">Generate a tailored cover letter in Norwegian based on your CV and the job description.</p>
          <button
            onClick={handleGenerate}
            disabled={loading || !cvText || !jdText}
            className="bg-green-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-green-700 disabled:bg-gray-400 transition flex items-center justify-center mx-auto min-h-[44px] focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2"
          >
            {loading ? (
              <>
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Generating... (this may take up to 2 mins)
              </>
            ) : 'Generate Cover Letter'}
          </button>
        </div>
      )}

      {error && (
        <div role="alert" className="mt-4 p-4 bg-red-100 text-red-700 rounded-lg">
          {error}
        </div>
      )}

      {generatedLetter && (
        <div className="mt-6 animate-fade-in">
          <h3 className="text-lg font-semibold mb-2 text-gray-700">Generated Cover Letter</h3>
          <textarea
            aria-label="Generated cover letter"
            className="w-full h-96 p-4 border rounded-lg focus:ring-2 focus:ring-green-500 text-gray-800 font-serif leading-relaxed"
            value={generatedLetter}
            onChange={(e) => setGeneratedLetter(e.target.value)}
          />
          <div className="mt-4 flex flex-wrap gap-4 justify-end">
             <button
              onClick={handleCopy}
              aria-label="Copy cover letter to clipboard"
              className="px-4 py-3 text-gray-600 hover:text-gray-900 border border-gray-300 rounded-md hover:bg-gray-50 flex items-center space-x-2 min-h-[44px] focus:outline-none focus:ring-2 focus:ring-green-500"
            >
              {isCopied ? <Check className="h-4 w-4 text-green-500" /> : <Copy className="h-4 w-4" />}
              <span>{isCopied ? 'Copied!' : 'Copy to Clipboard'}</span>
            </button>
            <button
              onClick={handleDownload}
              aria-label="Download cover letter as text file"
              className="px-4 py-3 text-gray-600 hover:text-gray-900 border border-gray-300 rounded-md hover:bg-gray-50 flex items-center space-x-2 min-h-[44px] focus:outline-none focus:ring-2 focus:ring-green-500"
            >
              <Download className="h-4 w-4" />
              <span>Download .txt</span>
            </button>
             <button
              onClick={() => setGeneratedLetter('')}
              className="px-4 py-3 text-gray-500 hover:text-gray-700 min-h-[44px] focus:outline-none focus:ring-2 focus:ring-gray-500"
            >
              Start Over
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CoverLetterGenerator;

'use client';

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import GapAnalysisDisplay from '../../components/analysis/GapAnalysisDisplay';
import CoverLetterGenerator from '../../components/analysis/CoverLetterGenerator';
import { createClient } from '@/utils/supabase/client';
import toast from 'react-hot-toast';

export default function AnalysisPage() {
  const [cvText, setCvText] = useState('');
  const [jdText, setJdText] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const API_URL = process.env.NEXT_PUBLIC_API_URL || 'http://127.0.0.1:8000';

  useEffect(() => {
    // Fetch latest CV and JD on mount
    const fetchData = async () => {
      try {
        const supabase = createClient();
        const { data: { session } } = await supabase.auth.getSession();
        const token = session?.access_token;
        
        if (!token) {
            console.log("No token found in AnalysisPage useEffect");
            return;
        }

        const config = {
          headers: { Authorization: `Bearer ${token}` }
        };

        // Fetch CV
        try {
          console.log("Fetching latest CV...");
          const cvRes = await axios.get(`${API_URL}/api/cv/latest`, config);
          console.log("CV Response:", cvRes.data);
          if (cvRes.data && cvRes.data.extracted_text) {
            setCvText(cvRes.data.extracted_text);
            toast.success('Loaded latest CV');
          } else {
             console.log("CV found but no extracted_text");
          }
        } catch (e: any) {
            console.error("Error fetching CV:", e.response?.data || e.message);
        }

        // Fetch JD
        try {
          console.log("Fetching latest JD...");
          const jdRes = await axios.get(`${API_URL}/api/job-description/latest`, config);
          console.log("JD Response:", jdRes.data);
          if (jdRes.data && jdRes.data.content) {
            setJdText(jdRes.data.content);
            toast.success('Loaded latest Job Description');
          }
        } catch (e: any) {
            console.error("Error fetching JD:", e.response?.data || e.message);
        }

      } catch (err) {
        console.error("Error loading data", err);
      }
    };
    fetchData();
  }, []);

  const handleAnalyze = async () => {
    setLoading(true);
    setError(null);
    try {
      const supabase = createClient();
      const { data: { session } } = await supabase.auth.getSession();
      const token = session?.access_token;
      
      const headers = token ? { Authorization: `Bearer ${token}` } : {};

      const response = await axios.post(`${API_URL}/ai/analyze-gap/`, {
        cv_text: cvText,
        job_description: jdText
      }, { headers });
      
      setResult(response.data);
    } catch (err: any) {
      setError(err.message || 'An error occurred');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4 md:p-8 max-w-7xl">
      <h1 className="text-3xl font-bold mb-6 text-white">Application Analysis</h1>
      
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Left Column: Inputs and Analysis */}
        <div className="space-y-6">
          <div className="bg-white p-6 rounded-lg shadow-sm">
            <h2 className="text-xl font-semibold mb-4 text-gray-700">1. Inputs</h2>
            <div className="space-y-4">
              <div>
                <label htmlFor="cv-text" className="block text-sm font-medium text-gray-700 mb-2">Your CV Text</label>
                <textarea
                  id="cv-text"
                  className="w-full h-48 p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-800 bg-white placeholder-gray-400"
                  value={cvText}
                  onChange={(e) => setCvText(e.target.value)}
                  placeholder="Paste your CV content here..."
                />
              </div>
              <div>
                <label htmlFor="jd-text" className="block text-sm font-medium text-gray-700 mb-2">Job Description</label>
                <textarea
                  id="jd-text"
                  className="w-full h-48 p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-800 bg-white placeholder-gray-400"
                  value={jdText}
                  onChange={(e) => setJdText(e.target.value)}
                  placeholder="Paste the job description here..."
                />
              </div>
              
              <button
                onClick={handleAnalyze}
                disabled={loading || !cvText || !jdText}
                className="w-full bg-blue-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-700 disabled:bg-gray-400 transition focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
              >
                {loading ? 'Analyzing...' : 'Analyze Gap'}
              </button>
            </div>
          </div>

          <GapAnalysisDisplay result={result} isLoading={loading} error={error} />
        </div>

        {/* Right Column: Generated Letter */}
        <div className="lg:border-l lg:pl-8 lg:border-gray-200">
           {(cvText && jdText) ? (
             <CoverLetterGenerator cvText={cvText} jdText={jdText} />
           ) : (
             <div className="bg-gray-50 p-6 rounded-lg border-2 border-dashed border-gray-300 text-center text-gray-500 h-full flex items-center justify-center">
                <p>Enter your CV and a Job Description to generate a cover letter.</p>
             </div>
           )}
        </div>
      </div>
    </div>
  );
}

